import { computed } from 'vue';
import { useRoute } from 'vue-router';

// Helper function to determine if the link is active

export default {
  __name: 'AppHeader',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const isActive = path => {
      return route.path === path;
    };

    // Define all navigation links
    const links = [{
      name: 'Home',
      path: '/'
    }, {
      name: 'Admin',
      path: '/admin'
    }, {
      name: 'Login',
      path: '/login'
    }, {
      name: 'Register',
      path: '/register'
    }];

    // Use Vue Router to get the current route
    const route = useRoute();

    // Filter links based on user authentication
    const filteredLinks = computed(() => {
      const user = localStorage.getItem('user');
      return links.filter(link => {
        if (link.path === '/admin' || link.path === '/register') {
          return user; // Show if user is authenticated
        }
        return true; // Always show other links
      });
    });
    const __returned__ = {
      isActive,
      links,
      route,
      filteredLinks,
      computed,
      get useRoute() {
        return useRoute;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
